SELECT IFNULL(bl.id,'') AS id, IFNULL(bl.line_id,'') AS lineid, IFNULL(bl.linkdir,'') AS linkdir, IFNULL(bl.line_name ,'')AS linename, IFNULL(bl.line_name_bd,'') AS linenamebd, IFNULL(bl.price ,'')AS price, IFNULL(bl.line_type ,'')AS linetype, IFNULL(bl.dictionary_id,'') AS dictionaryid, IFNULL(bl.installation_number,'') AS installationnumber, IFNULL(bl.match_number,'') AS matchnumber, IFNULL(dc.dictionary_name ,'') AS dictionaryname, IFNULL(bl.dictionary_id,'')AS dictionaryid, IFNULL(bl.create_time ,'')AS createtime FROM bus_line bl LEFT JOIN city ct ON ct.id = bl.city_id LEFT JOIN dictionary dc ON dc.id = bl.dictionary_id WHERE 1=1
